radio.onDataPacketReceived( ({ receivedString }) =>  {
    if (receivedString.substr(0, 2).compare("ZW") == 0) {
        Temp = receivedString
        if (Temp.substr(6, 2).compare("00") == 0) {
            doNetDeal()
        }
        if (Temp.substr(3, 2).compare(ID) == 0 || receivedString.substr(3, 2).compare("FF") == 0) {
            if (Temp.substr(6, 2).compare("01") == 0) {
                doRobotCtr()
            } else if (Temp.substr(6, 2).compare("02") == 0) {
                doServoCtr()
            } else if (Temp.substr(6, 2).compare("03") == 0) {
                doMusic()
            } else if (Temp.substr(6, 2).compare("04") == 0) {
                doCarColor()
            } else if (Temp.substr(6, 2).compare("05") == 0) {
                doArmColor()
            } else if (Temp.substr(6, 2).compare("06") == 0) {
                doLineColor()
            } else if (Temp.substr(6, 2).compare("07") == 0) {
                g_RGBMode = parseInt(Temp.substr(9, 1))
            } else if (Temp.substr(6, 2).compare("08") == 0) {
                doArmCatch()
            } else if (Temp.substr(6, 2).compare("09") == 0) {
                g_Mode = parseInt(Temp.substr(9, 1))
                if (g_Mode == 0) {
                    HelloBot.CarCtrl(HelloBot.CarState.Car_Stop)
                }
            } else if (receivedString.substr(6, 2).compare("0A") == 0) {
                doWJJ()
            }
        }
    }
})
